settings.custom_css:
/*** Home Portfolio Section ***/
selector .entry-main{
    box-shadow: 0 10px 35px rgba(0,0,0,0.29);
    border-radius:10px;
    max-width: 86%;
    margin: auto;
    position: absolute;
    bottom: -59px;
    background: #fff;
    left: 0;
    right: 0;
    transition: all 1100ms cubic-bezier(0.19, 1, 0.22, 1);
}

selector .aux-col:hover .entry-title a, selector .aux-col:hover .entry-tax a  {
    color: #fff!important;
    transition: all 1100ms cubic-bezier(0.19, 1, 0.22, 1);
}
selector .aux-isotope-ready {
    overflow: visible;
}
@media only screen and (max-width: 1406px)and (min-width: 1025px)  {
selector  .entry-title a{
    font-size: 22px!important;
} 
selector .entry-tax {
    font-size: 16px;
}
selector .entry-main {
    padding: 4% 10% 1% 13% !important;
}
}
selector .aux-pagination{
    display: none;
}
@media only screen and (max-width: 767px) {
    selector .aux-iso-item {
    padding-top:90px;
}
selector .aux-portfolio-columns .entry-header{   max-width: calc(100% - 20px);
}
}
selector .aux-col:hover .entry-main{background: var(--e-global-color-secondary);
    background: linear-gradient(180deg, var(--e-global-color-secondary) 4%, var(--e-global-color-primary) 84%);
    box-shadow: 0 3px 25px #4D0AA480;
    transition: all 1100ms cubic-bezier(0.19, 1, 0.22, 1);
    transform: translateY(-35px);
}

/*** Home Testimonial Section ***/
selector .col-title{
    position: absolute;
    bottom: 37px;
    right: 0;
    left: 0;
    max-width: 366px;
    margin: auto;
    display: inline-block;
}
selector .col-title:after{
    content: '';
    background: var(--e-global-color-secondary);
    background: linear-gradient(277deg, var(--e-global-color-secondary) 4%, var(--e-global-color-primary) 84%);
    width: 30px;
    height: 3px;
    position: absolute;
    left: 44px;
    top: 15px;
}
selector .col-subtitle{
    position: relative;
    z-index: 1;
    top: 15px;
}
selector .col-subtitle:after{
    content: '';
    background: var(--e-global-color-secondary);
    background: linear-gradient(180deg, var(--e-global-color-secondary) 4%, var(--e-global-color-primary) 84%);
    position: absolute;
    width: 102px;
    height: 102px;
    top: -19px;
    left:0%;
    right: 0;
    z-index: -1;
    border-radius: 100px;
    box-shadow: 0 3px 20px #4D0AA480;
    max-width: 100%;
    margin: auto;
}
@media only screen and  (max-width: 767px)  {
selector {
    margin-left: auto!important;
    margin-right: auto!important;
}
}

